import React from 'react';
import Song from './components/Song';
import songData from './components/Data';
import Current from "./components/CurrentlyPlaying";
import Next from "./components/NextButton";
import Previous from "./components/PreviousButton";
import PausePlayButton from "./components/PausePlayButton";
import RecentlyPlayed from "./components/RecentlyPlayed";

import './styles/App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.placeHolder = this.placeHolder.bind(this);
    this.pauseSong = this.pauseSong.bind(this);
    this.switchSong = this.switchSong.bind(this);
    this.getRandomSong = this.getRandomSong(this);

    this.state = {
      nowPlaying: { name: 'Nothing', albumArt: '', spotifyLink: ''},
      prevSong: {},
      isPlaying: false,
      songs : songData.songs,
      recent : songData.songs
    }
  }


  clearRecent() {
    let clear = [];
    this.setState({recent : clear});
  }

  playCurrentSong() {
  }

  getRandomSong() {
    let nextSong = this.state.songs[Math.floor(Math.random()* this.state.songs.length)];
    console.log(nextSong.name);
    let combined = this.state.recent.concat([nowPlaying]);
    this.setState({nowPlaying : nextSong, prevSong : this.state.nowPlaying,
        isPlaying : true, recent : combined});
    console.log(this.state.nowPlaying.name);
  }

  switchSong() {

  }

  pauseSong() {
    this.setState(prevState => {isPlaying : !prevState.isPlaying})
  }

  placeHolder() {

  }

  render() {
    return (
      <div className="flex-wrapper">
        <h2> BETTER THAN SPOTIFY </h2>
        <div className="songDisplay">
          {this.state.songs.map(song => <Song title={song.name} image="../icons/clock.svg"/>)}
        </div>
        <div className="current">
          <Current title='Current Song' />
          <div className="buttonDisplay">
            <Previous isPlaying={this.state.isPlaying} onClick={this.placeHolder}/>
            <PausePlayButton isPlaying={this.state.isPlaying} onClick={this.pauseSong}/>
            <Next isPlaying={this.state.isPlaying} onClick={this.getRandomSong}/>
          </div>
          <div className="recent">
            <RecentlyPlayed songList={this.state.recent} />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
